package Input;

import javax.swing.JFrame;

public class jframe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		JFrame frame = new JFrame();//new frame created
		frame.setSize(500, 499);//dimension of the frame
		frame.setTitle("My FirstFrame");//name of the frame
		frame.setDefaultCloseOperation(frame.EXIT_ON_CLOSE);
		frame.setVisible(true);//it will set the frame to working condition
	}

}
